import React,{useState} from 'react';
import './info.css'

import {useSelector } from 'react-redux';
import {selectData} from './reducer';


import {store} from './store.js'


export const Info = () => {

  const data = useSelector(selectData);
  // alert(data.bgcolor1);

  const x1STYLE = {
    backgroundColor:  data.bgcolor1  
  };  


  const x2STYLE = {
    backgroundColor:  data.bgcolor2
  };  



  return (
        <div className="contentContainer">
        <div className="projector"> </div>

        <div className="info">


          <table>
            <tr>
              <td> The first color is <u>#21dd2d</u> </td>
              <td> <div style={x1STYLE} className="color1"></div> </td>
            </tr>
            <tr>
              <td> The second color is  <u>#21dd2d</u> </td>
              <td> <div className="color2"></div> </td>
            </tr>

            <tr><td align="center" rowSpan='2'>
              <input 
                className="copyfrom"
                value="background: linear-gradient(to left,yellow,magenta)"
              ></input>
            </td></tr>
          </table>

        </div>
      </div>
    );
}
