                <button onClick={handleGenerate}className="generatorButton" onClick=''>
                    <div className="generatorContainer">
                        <div>Generate</div> 
                        <div className="generatorIcon"></div>
                    </div> 
                </button>
            </div>
        </div>
    );
}
